package my.topcoder;public class CyclesInPermutations {    public int maxCycle(int[] board) {        int max = -1;        for (int i = 0; i < board.length; i++) {            int j = board[i];            int m = 1;            int k = board[j - 1];            while (k != j) {                int g = board[k - 1];                k = g;                ++m;            }            max = Math.max(max, m);        }        return max;    }}